mapper.select:

select a.id, a.user_code, a.dep_code, a.create_date, a.`state`, a.task_code, a.task_name, a.task_type,
a.run_class, a.task_timer, a.remark, a.remark2,a.remark3,b.username user_name,c.shortname dep_name_abb,c.name dep_name
from sys_quartz_config a
left join sys_user b
on a.user_code=b.id
left join sys_depart c
on a.dep_code=c.dep_code
where a.dep_code like #{depCode}
and a.user_code = #{userId}
and a.task_type = #{taskType}


select a.id, a.user_code, a.dep_code, a.create_date, a.`state`, a.task_code, a.task_name, a.task_type,
a.run_class, a.task_timer, a.remark, a.remark2,a.remark3,b.username user_name,c.shortname dep_name_abb,c.name dep_name
from sys_quartz_config a
left join sys_user b
on a.user_code=b.id
left join sys_depart c
on a.dep_code=c.dep_code
where a.dep_code like #{depCode}
and a.user_code like #{userId}
and a.task_type like #{taskType}
and a.`state` = #{state}

delete from sys_quartz_config
where id in

  #{id}

update sys_quartz_config
set
`state` = #{state}
where ID IN

  #{id}
